mapper.select:
select * from blog where 1 = 1

	and title = #{title}


	and content = #{content}


	and owner = #{owner}

select * from blog where 1 = 1

	
		and title = #{title}
	
	                
		and content = #{content}
	
	               
		and owner = "owner1"

select * from blog

	
		title = #{title}
	
	
		and content = #{content}
	
	
		or owner = #{owner}

select * from blog

	
		title = #{title}
	
	
		and content = #{content}
	
	
		and owner = #{owner}

mapper.update:
update blog

	
		title = #{title},
	
	
		content = #{content},
	
	
		owner = #{owner}
	

where bid = #{bid}
